import React, { useState, useContext } from 'react'
import { useFormik } from 'formik'
import * as yup from 'yup'


import register from '../../assets/imagens/register.png'
import registered from '../../assets/imagens/registered.png'

import {
    Button, CssBaseline, TextField, Typography,
    Container, makeStyles, InputAdornment, IconButton, Grid,
    Link
} from '@material-ui/core'

import { AuthContext } from '../../context/AuthContext'
import EmailIcon from '@material-ui/icons/Email'
import Visibility from '@material-ui/icons/Visibility'
import VisibilityOff from '@material-ui/icons/VisibilityOff'

const Form = () => {

    const {user} = useContext(AuthContext)

    const {
        signIn,
    } = useContext(AuthContext)

    const classes = useStyles()
    const [show, setShow] = useState(false)

    const initialFormState = {
        email: '',
        password: ''
    }

    const validationSchema = yup.object().shape({
        email: yup.string().email('Entre com um e-mail válido!').required('O e-mail é obrigatório!'),
        password: yup.string('Entre com sua senha').required('A senha é obrigatória!'),
    })

    const formik = useFormik({
        initialValues: initialFormState,
        validationSchema: validationSchema,
        onSubmit: async (values) => {
            await signIn(values.email.trim(), values.password.trim())
        }
    })

    return (
        <Container component="main" maxWidth="xs">
            <CssBaseline />
            <div className={classes.paper}>
                {user && 
                    <div>
                        <img className={classes.logo} src={registered} alt='' />

                        <p align="center">
                            Conectado como:
                            <h2 className={classes.title}>
                                {user?.username}
                            </h2>
                        </p>
                        <Button
                            type="submit"
                            fullWidth
                            variant="contained"
                            color="primary"
                        >
                            Limpar dados e Sair
                        </Button>
                    </div>
                }
                {!user &&
                    <div>
                        <img className={classes.logo} src={register} alt='' />

                        <form className={classes.form} noValidate onSubmit={formik.handleSubmit}>
                            <TextField
                                variant="outlined"
                                margin="normal"
                                required
                                fullWidth
                                id="email"
                                label="E-mail"
                                name="email"
                                autoComplete="email"
                                autoFocus
                                value={formik.values.email}
                                onChange={formik.handleChange}
                                error={formik.touched.email && Boolean(formik.errors.email)}
                                helperText={formik.touched.email && formik.errors.email}
                                InputProps={{
                                    endAdornment: (
                                        <InputAdornment position="end">
                                            <IconButton color='default'>
                                                <EmailIcon />
                                            </IconButton>
                                        </InputAdornment>
                                    )
                                }}
                            />
                            <TextField
                                id="password"
                                name="password"
                                label="Senha"
                                variant='outlined'
                                fullWidth
                                required
                                type={show ? 'text' : 'password'}
                                value={formik.values.password}
                                onChange={formik.handleChange}
                                autoComplete="current-password"
                                error={formik.touched.password && Boolean(formik.errors.password)}
                                helperText={formik.touched.password && formik.errors.password}
                            />

                            <Button
                                type="submit"
                                fullWidth
                                variant="contained"
                                color="primary"
                                className={classes.submit}
                            >
                                Registrar
                            </Button>
                        </form>
                    </div>
                }
            </div>
        </Container>
    )
}

export default Form

const useStyles = makeStyles((theme) => ({
    paper: {
        marginTop: theme.spacing(8),
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
    logo: {
        width: 300,
        marginTop: '20%'
    },
    title: {
        flexGrow: 1,
        textTransform: 'capitalize',
        marginTop: '10%',
    },
}));
