import React, { useState, useContext, useEffect } from 'react'


import success from '../../assets/imagens/success.jpeg'

import {
    Button, CssBaseline, TextField, FormControlLabel, Checkbox, Link,
    Grid, Typography, Container, makeStyles, CircularProgress,
    Snackbar, SnackbarContent, InputAdornment, IconButton
} from '@material-ui/core'

import { AuthContext } from '../../context/AuthContext'

const Success = () => {

    const {user} = useContext(AuthContext)

    const classes = useStyles()

    return (
        <Container component="main" maxWidth="xs">
            <CssBaseline />
            <div className={classes.paper}>
                <img className={classes.logo} src={success} alt='' />

                <Typography component="h1" variant="h5" className={classes.title}>
                    Bem-vindo, João!
                </Typography>

                <p>Este foi o desafio desenvolvido por Vinicius Carneiro para o Connecta,
                    espero que tenha sido satisfatório e que eu possa ser selecionado para a vaga.
                </p>
                <p>Até a próxima :)</p>

                <Button
                        type="submit"
                        fullWidth
                        variant="contained"
                        color="primary"
                        className={classes.submit}
                    >
                        Limpar dados e Sair
                </Button>
            </div>
        </Container>
    )
}

export default Success

const useStyles = makeStyles((theme) => ({
    paper: {
        marginTop: theme.spacing(8),
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
    logo: {
        width: 300,
        marginTop: '20%'
    },
    title: {
        flexGrow: 1,
        textTransform: 'capitalize',
        marginTop: '10%',
    },
}));
