import React, { useState, useContext} from 'react'
import { useFormik } from 'formik'
import * as yup from 'yup'

import {AuthContext} from '../../context/AuthContext'
import EmailIcon from '@material-ui/icons/Email'
import Visibility from '@material-ui/icons/Visibility'
import VisibilityOff from '@material-ui/icons/VisibilityOff'

import welcome from '../../assets/imagens/welcome.jpeg'

import {
    Button, CssBaseline, TextField, Typography,
    Container, makeStyles, InputAdornment, IconButton, Grid,
    Link
} from '@material-ui/core'


const SignIn = () => {

    const {
        signIn
    } = useContext(AuthContext)

    const classes = useStyles()
    const [show, setShow] = useState(false)
    const email = localStorage.getItem('email')

    /*const logged = isLogged()

    const { email, time, token } = useParams()
    const { user } = useContext(AuthContext)

    const {
        signIn,
        loadingAuth,
        warningModal,
        handleCloseWarningModal,
        message
    } = useContext(AuthContext)
    const [forgotPassword, setForgotPassword] = useState(false)
    const [recovery, setRecovery] = useState(false)
    const [snackMessage, setSnackMessage] = useState('')
    const [snack, setSnack] = useState(false)
    const [snackColor, setSnackColor] = useState('')
    const [show, setShow] = useState(false)
    const [loading, setLoading] = useState(false)

    const handleOpenForgotPassword = () => setForgotPassword(true)
    const handleCloseForgotPassword = () => setForgotPassword(false)
    const handleOpenRecovery = () => setRecovery(true)
    const handleCloseRecovery = () => setRecovery(false)
    const handleOpenSnack = () => setSnack(true)
    const handleCloseSnack = () => setSnack(false)

    useEffect(() => {
        if (email && time && token) {
            validateLink()
        }
    }, [])

    const sendEmail = async (email) => {
        setLoading(true)
        const response = await api.sendEmailRecovery(email)
        if (response?.status === 200) {
            setSnackColor('#070')
            setSnackMessage('Verifique seu e-mail e siga as instruções para recuperar sua senha.')
            handleOpenSnack()
            handleCloseForgotPassword()
            setLoading(false)
        } else {
            setSnackColor('#da1e37')
            setSnackMessage('E-mail não localizado!')
            handleOpenSnack()
            setLoading(false)
        }

    }

    const validateLink = async () => {
        const response = await api.validateLink(email, time, token)
        if (response?.status === 200) {
            handleOpenRecovery()
        } else {
            setSnackColor('#da1e37')
            setSnackMessage('Link de redefinição de senha inválido ou expirado!')
            handleOpenSnack()
        }
    }

    const changePassword = async (password) => {
        setLoading(true)
        const response = await api.setNewPassword(email, time, token, password)
        if (response?.status === 200) {
            setSnackColor('#070')
            setSnackMessage('Nova senha salva com sucesso!')
            handleOpenSnack()
            handleCloseRecovery()
            setLoading(false)
        } else {
            setSnackColor('#da1e37')
            setSnackMessage('Link de redefinição de senha inválido ou expirado!')
            handleOpenSnack()
            setLoading(false)
            handleCloseRecovery()
        }
    }*/

    const initialFormState = {
        email: '',
        password: ''
    }

    const validationSchema = yup.object().shape({
        email: yup.string().email('Entre com um e-mail válido!').required('O e-mail é obrigatório!'),
        password: yup.string('Entre com sua senha').required('A senha é obrigatória!'),
    })

    const formik = useFormik({
        initialValues: initialFormState,
        validationSchema: validationSchema,
        onSubmit: (values) => {
            if(values.email.length === 0 || values.password.length === 0) {
                alert('E-mail e/ou senha inválidos')
            } else if(values.email == 'desafio@connecta.com' && values.password == '123'){
                console.log('aqui2')
                localStorage.setItem('email', values.email)
                console.log(localStorage.getItem('email'))
            }
        }
    })

    const logout = () => {
        console.log(localStorage.getItem('email'))
        localStorage.clear()
        console.log(localStorage.getItem('email'))
    }

    return (
        <Container component="main" maxWidth="xs">
            <CssBaseline />
                {email &&
                    <div className={classes.paper}>
                        <img className={classes.logo} src={welcome} alt='' />
                        <p align="center">
                            Conectado como:
                            <h2 className={classes.title}>
                                {email}
                            </h2>
                        </p>
                        <form className={classes.form} noValidate onSubmit={logout}>
                            <Button
                                type="submit"
                                fullWidth
                                variant="contained"
                                color="primary"
                            >
                                Limpar dados e Sair
                            </Button>
                        </form>
                    </div>
                }
                {!email &&
                    <div className={classes.paper}>
                        <img className={classes.logo} src={welcome} alt='' />
                        <Typography component="h1" variant="h5" className={classes.title}>
                            REALIZE SEU LOGIN
                        </Typography> 
                        <form className={classes.form} noValidate onSubmit={formik.handleSubmit}>
                            <TextField
                                variant="outlined"
                                margin="normal"
                                required
                                fullWidth
                                id="email"
                                label="E-mail"
                                name="email"
                                autoComplete="email"
                                autoFocus
                                value={formik.values.email}
                                onChange={formik.handleChange}
                                error={formik.touched.email && Boolean(formik.errors.email)}
                                helperText={formik.touched.email && formik.errors.email}
                                InputProps={{
                                    endAdornment: (
                                        <InputAdornment position="end">
                                            <IconButton color='default'>
                                                <EmailIcon />
                                            </IconButton>
                                        </InputAdornment>
                                    )
                                }}
                            />
                            <TextField
                                id="password"
                                name="password"
                                label="Senha"
                                variant='outlined'
                                fullWidth
                                required
                                type={show ? 'text' : 'password'}
                                value={formik.values.password}
                                onChange={formik.handleChange}
                                autoComplete="current-password"
                                error={formik.touched.password && Boolean(formik.errors.password)}
                                helperText={formik.touched.password && formik.errors.password}
                                InputProps={{
                                    endAdornment: (
                                        <InputAdornment position="end">
                                            <IconButton
                                                color='default'
                                                onClick={() => setShow(!show)}
                                            >
                                                {show ? <Visibility /> : <VisibilityOff />}
                                            </IconButton>
                                        </InputAdornment>
                                    )
                                }}
                            />

                            <Button
                                type="submit"
                                fullWidth
                                variant="contained"
                                color="primary"
                                className={classes.submit}
                            >
                                Entrar
                            </Button>

                        </form>
                    </div>
                }
        </Container>
    );
}

export default SignIn

const useStyles = makeStyles((theme) => ({
    paper: {
        marginTop: theme.spacing(8),
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
    avatar: {
        margin: theme.spacing(1),
        backgroundColor: theme.palette.secondary.main,
    },
    form: {
        width: '100%',
        marginTop: theme.spacing(1),
    },
    submit: {
        margin: theme.spacing(3, 0, 2),
        backgroundColor: '#070',

        '&:hover': {
            background: '#005200'
        },
    },
    logo: {
        width: 300,
        marginTop: '20%'
    },
    title: {
        flexGrow: 1,
        textTransform: 'capitalize',
        marginTop: '10%',
    },
    link: {
        fontSize: 20
    }
}));
